<?php/** * Product indexing, caching & searching features concept is taken from open source 'Advanced wp Search' Wp plugin by ILLID. *///include_once( 'includes/class-wpwbot-cache.php' );include_once( 'includes/class-wpwbot-table.php' );include_once( 'includes/class-wpwbot-search.php' );function wpbo_search_site() {		$results = new WP_Query( array(		'post_type'     => array( 'post', 'page' ),		'post_status'   => 'publish',		'nopaging'      => true,		'posts_per_page'=> 10,		's'             => stripslashes( $_POST['keyword'] ),	) );			$response = array();	$response['status'] = 'fail';		if ( !empty( $results->posts ) ) {		$response['status'] = 'success';		$response['html'] = '<div class="wpb-search-result">';		$response['html'] .= '<p>We have found '.count($results->posts).' results for <b>'.$_POST['keyword'].'</b>.</p>';		foreach ( $results->posts as $result ) {			$response['html'] .= '<a href="'.$result->guid.'" target="_blank">'.$result->post_title.'</a>';		}		$response['html'] .='</div>';	}else{		$texts = unserialize(get_option('qlcd_wp_chatbot_no_result'));		$response['html'] = $texts[array_rand($texts)];	}		echo json_encode($response);	die();}add_action( 'wp_ajax_wpbo_search_site',        'wpbo_search_site' );add_action( 'wp_ajax_nopriv_wpbo_search_site', 'wpbo_search_site' );add_action( 'wp_ajax_wpbo_search_responseby_intent',        'qc_wpbo_search_responseby_intent' );add_action( 'wp_ajax_nopriv_wpbo_search_responseby_intent', 'qc_wpbo_search_responseby_intent' );function qc_wpbo_search_responseby_intent(){	global $wpdb;	$keyword = sanitize_text_field($_POST['keyword']);	$table = $wpdb->prefix.'wpbot_response';	$result = $wpdb->get_row("SELECT `response` FROM `wp_wpbot_response` WHERE 1 and `intent` = '".$keyword."'");		$response = array('status'=>'fail');		if(!empty($result)){		$response['status'] = 'success';		$response['html'] = $result->response;	}	echo json_encode($response);	die();}add_action( 'wp_ajax_wpbo_search_response',        'qc_wpbo_search_response' );add_action( 'wp_ajax_nopriv_wpbo_search_response', 'qc_wpbo_search_response' );function qc_wpbo_search_response(){		global $wpdb;	$keyword = sanitize_text_field($_POST['keyword']);	$table = $wpdb->prefix.'wpbot_response';	$response_result = array();	$status = array('status'=>'fail', 'multiple'=>false);			$results = $wpdb->get_results("SELECT `query`, `response` FROM `$table` WHERE 1 and `query` = '".$keyword."'");		if(!empty($results)){		foreach($results as $result){						$response_result[] = array('query'=>$result->query, 'response'=>$result->response, 'score'=>1);					}	}	if(empty($response_result)){				$results = $wpdb->get_results("SELECT `query`, `response`, MATCH(`query`,`keyword`,`response`) AGAINST('".$keyword."' IN NATURAL LANGUAGE MODE) as score FROM $table WHERE MATCH(`query`,`keyword`,`response`) AGAINST('".$keyword."' IN NATURAL LANGUAGE MODE) order by score desc");		$weight = get_option('qc_bot_str_weight')?get_option('qc_bot_str_weight'):'0.4';		$weight = 0;		if(!empty($results)){			foreach($results as $result){				if($result->score >= $weight){					$response_result[] = array('query'=>$result->query, 'response'=>$result->response, 'score'=>$result->score);				}			}		}			}		if(empty($response_result)){		$wpdb->query("ALTER TABLE $table ADD FULLTEXT(`query`)");        $wpdb->query("ALTER TABLE $table ADD FULLTEXT(`keyword`)");		$wpdb->query("ALTER TABLE $table ADD FULLTEXT(`response`)");		$results = $wpdb->get_results("SELECT `query`, `response`, MATCH(`query`) AGAINST('".$keyword."' IN NATURAL LANGUAGE MODE) as score FROM $table WHERE MATCH(`query`) AGAINST('".$keyword."' IN NATURAL LANGUAGE MODE) order by score desc limit 15");		$weight = get_option('qc_bot_str_weight')?get_option('qc_bot_str_weight'):'0.4';		$weight = 0;		if(!empty($results)){			foreach($results as $result){				if($result->score >= $weight){					$response_result[] = array('query'=>$result->query, 'response'=>$result->response, 'score'=>$result->score);				}			}		}	}	if(empty($response_result)){		$results = $wpdb->get_results("SELECT `query`, `response`, MATCH(`keyword`) AGAINST('".$keyword."' IN NATURAL LANGUAGE MODE) as score FROM $table WHERE MATCH(`keyword`) AGAINST('".$keyword."' IN NATURAL LANGUAGE MODE) order by score desc limit 15");		$weight = get_option('qc_bot_str_weight')?get_option('qc_bot_str_weight'):'0.4';		$weight = 0;		if(!empty($results)){			foreach($results as $result){				if($result->score >= $weight){					$response_result[] = array('query'=>$result->query, 'response'=>$result->response, 'score'=>$result->score);				}			}		}	}	if(!empty($response_result)){		if(count($response_result)>1){			$status = array('status'=>'success', 'multiple'=>true, 'data'=>$response_result);		}else{			$status = array('status'=>'success', 'multiple'=>false, 'data'=>$response_result);		}	}	echo json_encode($status);	die();}